import React, { Component } from "react";

import { connect } from "react-redux";
import { deleteBook } from "../../services/index";

import "./../../assets/css/Style.css";
import {
  Card,
  Table,
  Image,
  ButtonGroup,
  Button,
  InputGroup,
  FormControl,
} from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faList,
  faEdit,
  faTrash,
  faStepBackward,
  faFastBackward,
  faStepForward,
  faFastForward,
  faSearch,
  faTimes,
} from "@fortawesome/free-solid-svg-icons";
import { Link } from "react-router-dom";
import MyToast from "../MyToast";
import axios from "axios";

class ProductList extends Component {
  constructor(props) {
    super(props);
    this.state = {
      products: [],
    };
  }



  findAllBooks() {
    axios
      .get(
        "http://localhost:8081/rest/api/cart/{username}" 
      )
      .then((response) => response.data)
      .then((data) => {
        this.setState({
          products: data.content,
        });
      })
      .catch((error) => {
        console.log(error);
        localStorage.removeItem("jwtToken");
        this.props.history.push("/");
      });
  }

//   deleteBook = (bookId) => {
//     this.props.deleteBook(bookId);
//     setTimeout(() => {
//       if (this.props.bookObject != null) {
//         this.setState({ show: true });
//         setTimeout(() => this.setState({ show: false }), 3000);
//         this.findAllBooks(this.state.currentPage);
//       } else {
//         this.setState({ show: false });
//       }
//     }, 1000);
//   };

  render() {
    const { products } = this.state;

    return (
      <div> {this.findAllBooks()}
        <div style={{ display: this.state.show ? "block" : "none" }}>
          <MyToast
            show={this.state.show}
            message={"Book Deleted Successfully."}
            type={"danger"}
          />
        </div>
        <Card className={"border border-dark bg-dark text-white"}>
          <Card.Header>
            <div style={{ float: "left" }}>
              <FontAwesomeIcon icon={faList} /> Your Cart List
            </div>

          </Card.Header>
          <Card.Body>
            <Table bordered hover striped variant="dark">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Roast</th>
                  <th>Price</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {products.length === 0 ? (
                  <tr align="center">
                    <td colSpan="7">No Items in Cart.</td>
                  </tr>
                ) : (
                  products.map((p) => (
                    <tr key={p.id}>

                      <td>{p.name}</td>
                      <td>{p.roast}</td>
                      <td>{p.price}</td>
                      <td>
                        <ButtonGroup>
                          <Button
                            size="sm"
                            variant="outline-danger"

                          >
                            <FontAwesomeIcon icon={faTrash} />
                          </Button>
                        </ButtonGroup>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </Table>
          </Card.Body>

        </Card>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    productObject: state.p,
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    //deleteBook: (productId) => dispatch(deleteBook(productId)),
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductList);
