import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import authToken from "../utils/authToken";
import { Alert } from "react-bootstrap";
import productImage from '../productImage.json';
import axios from "axios";


const Home = () => {
  if (localStorage.jwtToken) {
    authToken(localStorage.jwtToken);
  }

  const auth = useSelector((state) => state.auth);
  //console.log(auth.username);

  const [products, ProductList] = useState("");
  const [values, setValues] = useState({
    productL : [],
    username: '',
    status: 'IN_PROGRESS',
    total: '',
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { productL, name, email, password } = values;
    const user = {username, name, email, password};

    await axios.post('${API)/signup', user);        
};

  async function onsubmit(order,username,status,total) {
    //const userId = auth.userId
    //e.preventDefault()
    axios.post("http://localhost:8081/rest/api/cart/${username}",{
      order: ['','',''],
      username : username,
      status: status,
      total: total

    }).then((response => {
       console.log("Status: ", response.status);
       console.log("Data: ", response.data);

     }));
  }

  useEffect(() => {
    // axios.post("http://localhost:8081/rest/api/cart/{username}",{
    //   order: [
    //     name === p.name,
    //     roast=== p.roast,
    //     price=== p.price
    //   ],
    //   username : auth.username,
    //   status: 'IN_PROGRESS',
    //   total: ''

    // }).then((response => {
    //    console.log("Status: ", response.status);
    //    console.log("Data: ", response.data);

    //  }));
    if (products === "") {
      axios.get("http://localhost:8081/rest/products").then((response) => {
        ProductList(response.data);
        console.log("data", response.data);
      });
    }
  }, [products]);



  return (
    <>
    <Alert style={{ backgroundColor: "#343A40", color: "#ffffff80" }}>
      Welcome {auth.username}
    </Alert>
        <div className="hero is-primary" text="light">
          <div className="hero-body container">
            <h4 className="title">Our Products</h4>
          </div>
        </div>
        <br />
        <div className="container"  >
          <div className="column columns is-multiline">
            {products &&
              products.map((p) => (
                <div className=" column is-half">
                  <div className="box">
                    <div className="media">
                      <div className="media-left">
                        {productImage.map(i => {
                          return( <img src={i.image}/>);
                        })}
                      </div>
                      <div className="media-content">
                      <b style={{ textTransform: "capitalize" }}>Coffee Name: {p.name}</b>
                      <div>Blend: {p.roast}</div>
                      <p>
                        <button onClick={()=>onsubmit(p,auth.username,'IN_PROGRESS',p.price)}>Add</button>
                      </p>
                      </div>
                    </div>

                  </div>
                </div>

              ))}
          </div>
        </div>
      </>
  );
};

export default Home;
