import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import authToken from "../utils/authToken";
import { Alert } from "react-bootstrap";
import productImage from '../productImage.json';
import axios from "axios";
import { Link } from "react-router-dom";
import { Component } from "react";

import { connect } from "react-redux";
import {
  submitProduct,
} from "../../services/index";

import { Card, Form, Button, Col, InputGroup, Image } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faSave,
  faPlusSquare,
  faUndo,
  faList,
  faEdit,
} from "@fortawesome/free-solid-svg-icons";

class Product extends Component {
  constructor(props) {
    super(props);
    this.state = this.initialState;
  }

  initialState = {
      userid : "",
      id: "",
      name: "",
      roast: "",
      price: "",
  };

  componentDidMount() {
    const productId = +this.props.match.params.id;
    // if (productId) {
    //   this.findBookById(bookId);
    // }
    // this.findAllLanguages();
  }

  submitProduct = (event) => {
    event.preventDefault();

    const product = {
      name: this.state.name,
      roast: this.state.roast,
      price: this.state.price,
    };

    this.props.saveProduct(userid, product);
    setTimeout(() => {
      if (this.props.productObject.product != null) {
        this.setState({ show: true, method: "post" });

      }
    }, 2000);
    this.setState(this.initialState);
    render() 
      return (
          <PopupOver func ={this.submitProduct } />
      );

  };



  // bookList = () => {
  //   return this.props.history.push("/list");
  // };
}

const Home = () => {
  if (localStorage.jwtToken) {
    authToken(localStorage.jwtToken);
  }

  const auth = useSelector((state) => state.auth);

  const [products, ProductList] = useState("");

  useEffect(() => {
    if (products === "") {
      axios.get("http://localhost:8081/rest/products").then((response) => {
        ProductList(response.data);
      });
    }
  }, [products]);

  return (
    <>
    <Alert style={{ backgroundColor: "#343A40", color: "#ffffff80" }}>
      Welcome {auth.username}
    </Alert>
        <div className="hero is-primary" text="light">
          <div className="hero-body container">
            <h4 className="title">Our Products</h4>
          </div>
        </div>
        <br />
        <div className="container"  >
          <div className="column columns is-multiline">
            {products &&
              products.map((p) => (
                <div className=" column is-half">
                  <div className="box">
                    <div className="media">
                      <div className="media-left">
                        {productImage.map(i => {
                          return( <img src={i.image}/>);
                        })}
                      </div>
                      <div className="media-content">
                      <b style={{ textTransform: "capitalize" }}>Coffee Name: {p.name}</b>
                      <div>Blend: {p.roast}</div>
                      <div>Price: ${p.price}/lb</div>
                      <p>
                        <button type="submit" >

                            Add to Cart

                        </button>
                      </p>
                      </div>
                    </div>

                  </div>
                </div>

              ))}
          </div>
        </div>
      </>
  );
};

export default Home;
