import * as BT from "./productTypes";
import axios from "axios";

//get all item in cart
export const fetchProduct = () => {
  return (dispatch) => {
    dispatch({
      type: BT.FETCH_PRODUCT_REQUEST,
    });
    axios
      .get("http://localhost:8081/rest/api/products")
      .then((response) => {
        dispatch(productSuccess(response.data));
      })
      .catch((error) => {
        dispatch(productFailure(error));
      });
  };
};

export const saveBook = (book) => {
  return (dispatch) => {
    dispatch({
      type: BT.SAVE_OOK_REQUEST,
    });
    axios
      .post("http://localhost:8081/rest/books", book)
      .then((response) => {
        dispatch(bookSuccess(response.data));
      })
      .catch((error) => {
        dispatch(bookFailure(error));
      });
  };
};

const productSuccess = (product) => {
  return {
    type: BT.PRODUCT_SUCCESS,
    payload: product,
  };
};

const productFailure = (error) => {
  return {
    type: BT.PRODUCT_FAILURE,
    payload: error,
  };
};
