import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import authToken from "../utils/authToken";
import { Alert } from "react-bootstrap";
import productImage from '../productImage.json';
import axios from "axios";

const Home = () => {
  if (localStorage.jwtToken) {
    authToken(localStorage.jwtToken);
  }

  const auth = useSelector((state) => state.auth);
  const [products, ProductList] = useState("");

  useEffect(() => {
    getCartApi()
    if (products === "") {
      axios.get("http://localhost:8081/rest/products").then((response) => {
        ProductList(response.data);
        console.log("data", response.data);
      });
    }
  }, [products]);

  const getCartApi = ()=>{		
		httpPost("api/cart/{username}",{})
		.then((res)=>{				
			res.json() .then(data=>{
				if(res.ok){
					patch({
						"type":"add_cart",
						"data":data
					})
					//alert("Successfully added..")
				}else{
					alert(data.message)
				}
			})
		},error=>{
			alert(error.message);
		}
		)
  }

  const addCartApi=(productObj)=>{
		let obj = {
			"productId":productObj.id,			
			"qty":"1",
			"price":productObj.price

		}
		httpPost("aapi/acart/{username}",obj)
		.then((res)=>{		
			res.json() .then(data=>{
				if(res.ok){
					patch({
						"type":"add_cart",
						"data":data
					})
					alert("Successfully added..")
				}else{
					alert(data.message)
				}
			})     
		}).catch(function(res){
			console.log("Error ",res);
			//alert(error.message);
		}
		)
	}

  return (
    <>
    <Alert style={{ backgroundColor: "#343A40", color: "#ffffff80" }}>
      Welcome {auth.username}
    </Alert>
        <div className="hero is-primary" text="light">
          <div className="hero-body container">
            <h4 className="title">Our Products</h4>
          </div>
        </div>
        <br />
        <div className="container"  >
          <div className="column columns is-multiline">
            {products &&
              products.map((p) => (
                <div className=" column is-half">
                  <div className="box">
                    <div className="media">
                      <div className="media-left">
                        {productImage.map(i => {
                          return( <img src={i.image}/>);
                        })}
                      </div>
                      <div className="media-content">
                      <b style={{ textTransform: "capitalize"}} >
                        Coffee Name: {p.name}
                      </b>
                      <div>Blend: {p.roast}</div>
                      <div>Price: ${p.price}</div>
                      <p>
                      <h5><a onClick={()=>addCartApi(p)} href="javascript:void(0)">Add Cart</a></h5>
                      </p>
                      </div>
                    </div>

                  </div>
                </div>

              ))}
          </div>
        </div>
      </>
  );
};

export default Home;
