import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import authToken from "../utils/authToken";
import { Alert } from "react-bootstrap";
import productImage from '../productImage.json';
import axios from "axios";


const Home = () => {
  if (localStorage.jwtToken) {
    authToken(localStorage.jwtToken);
  }

  const auth = useSelector((state) => state.auth);
  //console.log(auth.username);
  const [state,setState]=React.useState({
    order: {
      value: ['','','',''],
      error:null
    },
    username: {
      value:"{auth.username}",
      error:null
    },
    status:{
      value:"IN_PROGRESS",
      error:null
    },
    total:{
      value:"",
      error:99999
    }
  })

  const handleChange = (e) => {
    setState({ ...state, [e.target.name] : { value : e.target.value, error : null }})

  }

  const handleSubmit = () => {
    const payload = {
        order  : state.order.value, 
        username  : state.username.value, 
        status : state.status.value,
        total : state.total.value         
    }

    axios.post( "http://localhost:8081/rest/api/cart/{username}", payload)
        .then( response => {
            console.log(response);
            if ( response.status === 200 ){
                props.history.push('/home');
            }
        }).catch( err => console.log(err));
  }
  const { classes } = props;

  const [products, ProductList] = useState("");

  useEffect(() => {
    if (products === "") {
      axios.get("http://localhost:8081/rest/products").then((response) => {
        ProductList(response.data);
        console.log("data", response.data);
      });
    }
  }, [products]);



  return (
    <>
    <Alert style={{ backgroundColor: "#343A40", color: "#ffffff80" }}>
      Welcome {auth.username}
    </Alert>
        <div className="hero is-primary" text="light">
          <div className="hero-body container">
            <h4 className="title">Our Products</h4>
          </div>
        </div>
        <br />
        <div className="container"  >
          <div className="column columns is-multiline">
            {products &&
              products.map((p) => (
                <div className=" column is-half">
                  <div className="box">
                    <div className="media">
                      <div className="media-left">
                        {productImage.map(i => {
                          return( <img src={i.image}/>);
                        })}
                      </div>
                      <div className="media-content">
                      <b style={{ textTransform: "capitalize"}} name="order" onChange={handleChange} value={p.name}>
                        Coffee Name: {p.name}
                      </b>
                      <div>Blend: {p.roast}</div>
                      <div>Price: ${p.price}</div>
                      <p>
                        <button>Add</button>
                      </p>
                      </div>
                    </div>

                  </div>
                </div>

              ))}
          </div>
        </div>
      </>
  );
};

export default Home;
