import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import authToken from "../utils/authToken";
import { Alert } from "react-bootstrap";
import productImage from '../productImage.json';
import axios from "axios";


const Home = () => {
  if (localStorage.jwtToken) {
    authToken(localStorage.jwtToken);
  }

  const auth = useSelector((state) => state.auth);
  //console.log(auth.username);

  const [products, ProductList] = useState("");
  const [info, setInfo]=useState({
      productL : ['','',''],
      username: "",
      status: "",
      total: ""
  })

  function onsubmit(e) {
    e.preventDefault();
    axios.post("http://localhost:8081/rest/api/cart/test",{
      productL : info.productL,
      username: info.username,
      status: 'IN_PROGRESS',
      total: parseDouble(info.price)
      // productL : ['JACOBS','dark','7.99'],
      // username: "test",
      // status: 'IN_PROGRESS',
      // total: 7.99
    }).then(res=>{
      console.log(res.data);
    })
  }

  //   axios.post("http://localhost:8081/rest/api/cart/{username}",{
  //     productL : ['JACOBS','dark','7.99'],
  //     username: "test",
  //     status: 'IN_PROGRESS',
  //     total: 7.99,

  //   }).then((response => {

  //      console.log("Status: ", response.status);
  //      console.log("Data: ", response.data);

  //    }));
  // }

  useEffect(() => {
    const info2 = {
      productL : ['JACOBS','dark','7.99'],
      username: "test",
      status: 'IN_PROGRESS',
      total: 7.99,
    }

    if (products === "") {
      axios.get("http://localhost:8081/rest/products").then((response) => {
        ProductList(response.data);
        console.log("data", response.data);
      });
    }
  }, [products]);



  return (
    <>
    <Alert style={{ backgroundColor: "#343A40", color: "#ffffff80" }}>
      Welcome {auth.username}
    </Alert>
        <div className="hero is-primary" text="light">
          <div className="hero-body container">
            <h4 className="title">Our Products</h4>
            <button onClick={onsubmit}>Add</button>
            <form onSu>addtocart</form>
          </div>
        </div>
        <br />
        <div className="container"  >
          <div className="column columns is-multiline">
            {products &&
              products.map((p) => (
                <div className=" column is-half">
                  <div className="box">
                    <div className="media">
                      <div className="media-left">
                        {productImage.map(i => {
                          return( <img src={i.image}/>);
                        })}
                      </div>
                      <div className="media-content">
                      <b style={{ textTransform: "capitalize" }}>Coffee Name: {p.name}</b>
                      <div>Blend: {p.roast}</div>
                      <p>
                        <button >Add</button>
                      </p>
                      </div>
                    </div>

                  </div>
                </div>

              ))}
          </div>
        </div>
      </>
  );
};

export default Home;
