import React, { Component } from "react";

import { connect } from "react-redux";
import {
  saveBook,
  fetchBook,
  updateBook,
  fetchLanguages,
  fetchGenres,
} from "../../services/index";

import { Card, Form, Button, Col, InputGroup, Image } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faSave,
  faPlusSquare,
  faUndo,
  faList,
  faEdit,
} from "@fortawesome/free-solid-svg-icons";
import MyToast from "../MyToast";

class Product extends Component {
    constructor(props) {
      super(props);
      this.state = this.initialState;
    }

    initialState = {
        userid : "",
        id: "",
        name: "",
        roast: "",
        price: "",
    };

    componentDidMount() {
      const productId = +this.props.match.params.id;
      if (productId) {
        this.findBookById(bookId);
      }
      this.findAllLanguages();
    }

    submitProduct = (event) => {
      event.preventDefault();

      const product = {
        name: this.state.name,
        roast: this.state.roast,
        price: this.state.price,
      };

      this.props.saveProduct(userid, product);
      setTimeout(() => {
        if (this.props.productObject.product != null) {
          this.setState({ show: true, method: "post" });
          setTimeout(() => this.setState({ show: false }), 3000);
        } else {
          this.setState({ show: false });
        }
      }, 2000);
      this.setState(this.initialState);
    };



    bookList = () => {
      return this.props.history.push("/list");
    };

    render() {
      const { userid, name, roast, price} =
        this.state;

      return (
        <div>
          <div style={{ display: this.state.show ? "block" : "none" }}>
            <MyToast
              show={this.state.show}
              message={
                this.state.method === "put"
                  ? "Book Updated Successfully."
                  : "Book Saved Successfully."
              }
              type={"success"}
            />
          </div>
          <Card className={"border border-dark bg-dark text-white"}>

            <Form
              onReset={this.resetBook}
              onSubmit={this.state.id ? this.updateBook : this.submitBook}
              id="bookFormId"
            >
              <Card.Footer style={{ textAlign: "right" }}>
                <Button size="sm" variant="success" type="submit">
                  <FontAwesomeIcon icon={faSave} />{" "}
                  {this.state.id ? "Update" : "Save"}
                </Button>{" "}
                <Button size="sm" variant="info" type="reset">
                  <FontAwesomeIcon icon={faUndo} /> Reset
                </Button>{" "}
                <Button
                  size="sm"
                  variant="info"
                  type="button"
                  onClick={() => this.bookList()}
                >
                  <FontAwesomeIcon icon={faList} /> Book List
                </Button>
              </Card.Footer>
            </Form>
          </Card>
        </div>
      );
    }
  }

  const mapStateToProps = (state) => {
    return {
      bookObject: state.book,
    };
  };

  const mapDispatchToProps = (dispatch) => {
    return {
      saveBook: (book) => dispatch(saveBook(book)),
      fetchBook: (bookId) => dispatch(fetchBook(bookId)),
      updateBook: (book) => dispatch(updateBook(book)),
      fetchLanguages: () => dispatch(fetchLanguages()),
      fetchGenres: () => dispatch(fetchGenres()),
    };
  };

  export default connect(mapStateToProps, mapDispatchToProps)(Book);
